// src/App.js

import React, { useState } from 'react';
import { GoogleAuthProvider, signInWithPopup, signOut } from "firebase/auth";
import { auth } from './firebase'; // Import Firebase services
import './App.css';
import Chatbot from './Chatbot'; // Import the Chatbot component

function App() {
  const [user, setUser] = useState(null);  // To store the logged-in user

  // Function to handle Google Sign-In
  const handleGoogleSignIn = () => {
    const provider = new GoogleAuthProvider();
    signInWithPopup(auth, provider)
      .then((result) => {
        setUser(result.user);  // Set the signed-in user
      })
      .catch((error) => {
        console.error("Error during sign-in:", error);
      });
  };

  // Function to handle Sign-Out
  const handleSignOut = () => {
    signOut(auth)
      .then(() => {
        setUser(null);  // Clear the user state
      })
      .catch((error) => {
        console.error("Error during sign-out:", error);
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        {!user ? (
          <button onClick={handleGoogleSignIn} className="App-link">
            Sign in with Google
          </button>
        ) : (
          <div>
            <h2>Welcome, {user.displayName}</h2>
            <img src={user.photoURL} alt={user.displayName} width="100" />
            <p>Email: {user.email}</p>
            <button onClick={handleSignOut} className="App-link">
              Sign out
            </button>

            {/* Chatbot Component */}
            <Chatbot />
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
