// frontend/src/Chatbot.js
import React, { useState, useEffect, useRef } from 'react';
import '@chatscope/chat-ui-kit-styles/dist/default/styles.min.css';  // Import chat-ui styles
import {
  MainContainer,
  ChatContainer,
  MessageList,
  Message,
  MessageHeader,
  MessageInput,
  TypingIndicator,
  Avatar
} from '@chatscope/chat-ui-kit-react';

const Chatbot = ({ user }) => {
  const [input, setInput] = useState('');
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(false);
  const messageEndRef = useRef(null);

  // Scroll to the bottom when new messages arrive
  const scrollToBottom = () => {
    messageEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = {
      text: input,
      sender: 'user',
      timestamp: new Date().toLocaleTimeString(),
      profilePicture: user.photoURL || 'https://via.placeholder.com/40', // Use user profile picture or a placeholder
    };

    setMessages((prev) => [...prev, userMessage]);
    setLoading(true);
    setInput('');

    try {
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

      // Create the conversation context by combining all previous messages
      const conversationContext = messages.map((msg) => `${msg.sender}: ${msg.text}`).join('\n');

      const response = await fetch(`${API_URL}/api/chat`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          prompt: input,
          context: conversationContext
        }),
      });

      const data = await response.json();
      const aiMessage = {
        text: data.message,
        sender: 'ai',
        timestamp: new Date().toLocaleTimeString(),
        profilePicture: 'https://via.placeholder.com/40?text=AI', // Placeholder for AI profile picture
      };

      setMessages((prev) => [...prev, aiMessage]);
    } catch (error) {
      console.error('Error fetching AI response:', error);
      const errorMessage = {
        text: 'Error connecting to the AI service. Please try again later.',
        sender: 'error',
        timestamp: new Date().toLocaleTimeString(),
        profilePicture: 'https://via.placeholder.com/40?text=Err',
      };

      setMessages((prev) => [...prev, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="chat-container">
      <MainContainer responsive style={{ height: '100%' }}>
        <ChatContainer>
          <MessageList typingIndicator={loading ? <TypingIndicator content="AI is typing..." /> : null}>
            {messages.map((msg, index) => (
              <Message
              key={index}
              model={{
                message: msg.typing ? `${msg.text}...` : msg.text, // Show ellipsis for typing message
                sentTime: msg.timestamp,
                sender: msg.sender === 'user' ? 'You' : 'AI',
                direction: msg.sender === 'user' ? 'outgoing' : 'incoming',
                position: 'normal',
              }}
              avatarPosition={msg.sender === 'user' ? 'trailing' : 'leading'} // Position avatar to the right for user and left for AI
            >
              <MessageHeader
                sender={msg.sender === 'user' ? 'You' : 'AI'}
                sentTime={msg.timestamp} // Display timestamp in the header
              />
              <Avatar
                src={msg.profilePicture}
                name={msg.sender === 'user' ? 'You' : 'AI'}
                size="40" // Set avatar size
              />
            </Message>
            ))}
            <div ref={messageEndRef} />
          </MessageList>
          <MessageInput
            placeholder="Type your message here..."
            value={input}
            onChange={(value) => setInput(value)}
            onSend={sendMessage}
            attachButton={false}
            sendButton={!loading}  // Disable send button when loading
            disabled={loading}     // Disable input when loading
          />
        </ChatContainer>
      </MainContainer>
    </div>
  );
};

export default Chatbot;
